import React, { useState } from "react";
import { addItemToCart, removeItemFromCart } from "./CartHelper";



const SeatHelper = ({ seat }) => {
    console.log("this function has been called")
    const seatBooked = seat
        ? seat.booked
        : false

    const seatName= seat
    ? (Number(seat.row)-1)*10 + Number(seat.col)
    : "Anonymous"

   var clicked = false;
    const addToCart = () => {
        if (clicked) {
            console.log("ticket removed")

            removeItemFromCart(seat, ()=>{})
            console.log(seat)
            clicked = !clicked
        } else {
            addItemToCart(seat, ()=>{})
            console.log("ticket is added")
            clicked =  !clicked
        }

    }

    const showButton = (seatBooked) => {
        if (seatBooked){
            return(
                <button type = "button" className="btn btn-danger">{seatName}</button>
            )
        } else {
            if (clicked) {
                return (
                    <button onClick = {addToCart} type = "button" className="btn btn-primary">{seatName}</button>
                    )
            } else {
                return (
                    <button onClick = {addToCart} type = "button" className="btn btn-success">{seatName}</button>
                    )
            }

        }
    }

    return (
    <div className="rounded border border-success p-2">
        {showButton(seatBooked)}
    </div>
    );
};

export default SeatHelper;
