import React, { useState } from 'react'
import { Navigate } from 'react-router-dom';
import Base from "../core/Base";
import { signup } from './helper';


const Signin = () => {

    const [values, setValues]=  useState({
        name: "",
        email:"",
        error: "",
        success: false,
    });
    const {name, email, error, success} = values;

    const handleChange = name => event => {
        setValues({...values,error: false, [name]: event.target.value})
    }
    const row = 1; 
    const col = 1;
    const onSubmit=(event)=>{
        event.preventDefault();
        setValues({...values, error: false})
        signup({name,email,row, col})    // replace 1 and 5 with (row, col)
        .then((data)=> {
            console.log("DATA",data);
            if(data.email === email){
                setValues({
                    ...values,
                    name:"",
                    email: "",
                    error: "",
                    success: true
                })
            }
            else{
                setValues({
                    ...values,
                    error: true,
                    success: false
                })
            }
        }
        )
        .catch(err => console.log(err))


    }

    const successMessage =() => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div className="alert-success" style={{display: success ? "" : "none" }}>
                        New Account created successfully.

                    </div>
                </div>
            </div>
        )
    } 
    const errorMessage =() => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div className="alert alert-danger" style={{display: success ? "none" : "" }}>
                        something went wrong
                    </div>
                </div>
            </div>
        )
    } 

    const signUpForm=()=>{
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                      <div className="form-group">
                          <label className="text-light">
                              Name
                          </label>
                          <input type="text"
                          className="form-control"
                          values={name}
                          onChange={handleChange("name")}/>
                      </div>
                      <div className="form-group">
                          <label className="text-light">
                              Email
                          </label>
                          <input type="text"
                          className="form-control"
                          values={email}
                          onChange={handleChange("email")}/>
                      </div>

                      <button  className="btn btn-success btn-block">Submit</button>
                    </form>

                </div>
            </div>    
        )
    }





  return (
    <Base title = "Grab your Ticket" description = "Enter your email and name for confirmation">
    {successMessage()}
    {errorMessage()}
    {signUpForm()}
    <p className="text-white text-center">
                {JSON.stringify(values)}
            </p>
    </Base>
  )
}


export default Signin;
